mapper.select:
SELECT *
FROM altservice.comtnemplyrinfo
WHERE emplyr_id = #{altNo}
AND password = #{pwd}

mapper.delete:
UPDATE altservice.comtnemplyrinfo
SET DELETE_AT = 'Y'
WHERE emplyr_id = #{emplyr_id}

mapper.select:
SELECT COUNT(*) AS cnt
FROM altservice.comtnemplyrinfo
WHERE emplyr_id = #{emplyr_id}
